import React, { useState, useEffect } from 'react';
import AgentSidebar from './AgentSidebar';

function AgentDashboard({ token, userId }) {
  const [agentId, setAgentId] = useState(userId);
  const [tickets, setTickets] = useState([]);
  const [selectedTicketId, setSelectedTicketId] = useState(null);
  const [newComment, setNewComment] = useState('');
  const [editCommentId, setEditCommentId] = useState(null);
  const [editCommentText, setEditCommentText] = useState('');
  const [activeView, setActiveView] = useState('view');
  const [users, setUsers] = useState([]);

  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [passwordLoading, setPasswordLoading] = useState(false);
  const [passwordError, setPasswordError] = useState('');
  const [passwordSuccess, setPasswordSuccess] = useState('');

  useEffect(() => {
    if (!token || !agentId) return;

    setLoading(true);
    setError(null);

    // Fetch tickets assigned to this agent
    fetch(`http://localhost:8080/api/tickets`, {
      headers: {
        'Authorization': `Bearer ${token}`,
      },
    })
      .then(res => res.json())
      .then(data => {
        const assignedTickets = data.filter(ticket => ticket.assignedAgent && ticket.assignedAgent.id === agentId);
        setTickets(assignedTickets);
      })
      .catch(err => {
        console.error('Error fetching tickets:', err);
        setError('Failed to fetch tickets');
      })
      .finally(() => setLoading(false));
  }, [token, agentId]);

  useEffect(() => {
    if (activeView === 'view-users' && token) {
      fetch('http://localhost:8080/api/users', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
        .then(res => res.json())
        .then(data => setUsers(data))
        .catch(err => console.error('Error fetching users:', err));
    }
  }, [activeView, token]);

  useEffect(() => {
    if (activeView === 'profile' && token && agentId) {
      setLoading(true);
      setError(null);
      fetch(`http://localhost:8080/api/users/${agentId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
        .then(res => {
          if (!res.ok) throw new Error('Failed to fetch profile');
          return res.json();
        })
        .then(data => {
          setProfile(data);
          setError(null);
        })
        .catch(err => {
          console.error('Error fetching profile:', err);
          setError('Failed to fetch profile');
        })
        .finally(() => setLoading(false));
    }
  }, [activeView, token, agentId]);

  const handleChangePassword = (e) => {
    e.preventDefault();
    setPasswordLoading(true);
    setPasswordError('');
    setPasswordSuccess('');

    fetch(`http://localhost:8080/api/users/${agentId}/change-password`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        currentPassword,
        newPassword,
      }),
    })
      .then(res => {
        if (!res.ok) {
          return res.text().then(text => { throw new Error(text || 'Failed to change password'); });
        }
        return res.json();
      })
      .then(() => {
        setPasswordSuccess('Password changed successfully');
        setCurrentPassword('');
        setNewPassword('');
      })
      .catch(err => {
        setPasswordError(err.message);
      })
      .finally(() => {
        setPasswordLoading(false);
      });
  };

  const handleAddComment = () => {
    if (newComment.trim() && selectedTicketId) {
      // Post new comment to backend
      fetch('http://localhost:8080/api/comments', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
          body: JSON.stringify({
          ticketId: selectedTicketId,
          content: newComment,
        }),
      })
        .then(res => res.json())
        .then(newCommentData => {
          setTickets(tickets.map(ticket => {
            if (ticket.id === selectedTicketId) {
              return {
                ...ticket,
                comments: [...(ticket.comments || []), newCommentData],
              };
            }
            return ticket;
          }));
          setNewComment('');
        })
        .catch(err => console.error('Error adding comment:', err));
    }
  };

  const handleEditComment = (ticketId, commentId, text) => {
    setSelectedTicketId(ticketId);
    setEditCommentId(commentId);
    setEditCommentText(text);
  };

  const handleUpdateComment = () => {
    if (editCommentText.trim() && selectedTicketId && editCommentId !== null) {
      // Update comment in backend
      fetch(`http://localhost:8080/api/comments/${editCommentId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ content: editCommentText }),
      })
        .then(res => {
          if (res.ok) {
            setTickets(tickets.map(ticket => {
              if (ticket.id === selectedTicketId) {
                return {
                  ...ticket,
                  comments: ticket.comments.map(comment =>
                    comment.id === editCommentId ? { ...comment, content: editCommentText } : comment
                  ),
                };
              }
              return ticket;
            }));
            setEditCommentId(null);
            setEditCommentText('');
          } else {
            console.error('Failed to update comment');
          }
        })
        .catch(err => console.error('Error updating comment:', err));
    }
  };

  const handleChangeTicketState = (ticketId, newState) => {
    // Update ticket state in backend
    fetch(`http://localhost:8080/api/tickets/${ticketId}`, {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ state: newState }),
    })
      .then(res => {
        if (res.ok) {
          setTickets(tickets.map(ticket =>
            ticket.id === ticketId ? { ...ticket, state: newState } : ticket
          ));
        } else {
          console.error('Failed to update ticket state');
        }
      })
      .catch(err => console.error('Error updating ticket state:', err));
  };

  return (
    <div className="flex min-h-screen font-poppins bg-gray-100">
      <AgentSidebar onSelect={setActiveView} activeView={activeView} />
      <main className="flex-grow p-8 ml-64 max-w-6xl mx-auto">

        {error && <p className="text-red-600 mb-4">{error}</p>}

        {activeView === 'viewUsers' && (
          <section className="mb-8">
            <h2 className="text-2xl font-semibold mb-4 text-blue-800">Users</h2>
            <table className="min-w-full bg-white border border-gray-300 rounded">
              <thead>
                <tr>
                  <th className="border px-4 py-2 text-left">ID</th>
                  <th className="border px-4 py-2 text-left">Username</th>
                  <th className="border px-4 py-2 text-left">Role</th>
                </tr>
              </thead>
              <tbody>
                {users.map(user => (
                  <tr key={user.id} className="hover:bg-gray-100">
                    <td className="border px-4 py-2">{user.id}</td>
                    <td className="border px-4 py-2">{user.username}</td>
                    <td className="border px-4 py-2 capitalize">{user.role}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </section>
        )}

        {activeView === 'viewTickets' && (
          <>
            <section className="mb-8">
              <h2 className="text-2xl font-semibold mb-4 text-blue-800">Tickets (Total: {tickets.length})</h2>
              <table className="min-w-full bg-white border border-gray-300 rounded">
              <thead>
                <tr>
                  <th className="border px-4 py-2 text-left">ID</th>
                  <th className="border px-4 py-2 text-left">Title</th>
                  <th className="border px-4 py-2 text-left">Description</th>
                  <th className="border px-4 py-2 text-left">Status</th>
                  <th className="border px-4 py-2 text-left">Created By ID</th>
                  <th className="border px-4 py-2 text-left">Agent ID</th>
                </tr>
              </thead>
              <tbody>
                {tickets.map(ticket => (
                  <tr key={ticket.id} className="hover:bg-gray-100">
                    <td className="border px-4 py-2">{ticket.id}</td>
                    <td className="border px-4 py-2">{ticket.title}</td>
                    <td className="border px-4 py-2">{ticket.description}</td>
                    <td className="border px-4 py-2">{ticket.state}</td>
                    <td className="border px-4 py-2">{ticket.createdBy ? ticket.createdBy.id : 'null'}</td>
                    <td className="border px-4 py-2">
                      {ticket.assignedAgent
                        ? ticket.assignedAgent.id
                        : ticket.state === 'ASSIGNED'
                        ? 'Assigned'
                        : 'Unassigned'}
                    </td>
                  </tr>
                ))}
              </tbody>
              </table>
            </section>
          </>
        )}

        {activeView === 'assignTickets' && (
          <section className="mb-8">
            <h2 className="text-2xl font-semibold mb-4 text-blue-800">Assign Tickets</h2>
            <div className="mb-4">
              <label htmlFor="ticketSelect" className="block mb-1 font-medium">Select Ticket</label>
              <select
                id="ticketSelect"
                value={selectedTicketAssign || ''}
                onChange={(e) => setSelectedTicketAssign(e.target.value)}
                className="border border-gray-300 rounded px-3 py-2 w-full max-w-xs"
              >
                <option value="" disabled>Select a ticket</option>
                {openTickets.map(ticket => (
                  <option key={ticket.id} value={ticket.id}>
                    {ticket.title} (Assigned to: {ticket.assignedAgent ? ticket.assignedAgent.username : 'Unassigned'})
                  </option>
                ))}
              </select>
            </div>
            <div className="mb-4">
              <label htmlFor="agentSelect" className="block mb-1 font-medium">Assign to Agent</label>
              <select
                id="agentSelect"
                value={selectedAgent || ''}
                onChange={(e) => setSelectedAgent(e.target.value)}
                className="border border-gray-300 rounded px-3 py-2 w-full max-w-xs"
              >
                <option value="" disabled>Select an agent</option>
                {availableAgents.map(agent => (
                  <option key={agent.id} value={agent.id}>{agent.username}</option>
                ))}
              </select>
            </div>
            <button
              onClick={assignTicket}
              disabled={!selectedTicketAssign || !selectedAgent}
              className="bg-blue-600 text-white px-4 py-2 rounded disabled:opacity-50"
            >
              Assign Ticket
            </button>
          </section>
        )}

        {activeView === 'viewProfile' && (
          <section className="mb-8 flex flex-col items-center justify-center">
            <h2 className="text-3xl font-semibold mb-6 text-blue-800">Profile</h2>
            {loading && <p>Loading profile...</p>}
            {error && <p className="text-red-600">{error}</p>}
            {profile && (
              <div className="flex flex-col items-center bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
                <div className="mb-6">
                  <div className="w-24 h-24 rounded-full bg-blue-600 flex items-center justify-center shadow-lg mx-auto overflow-hidden">
                    <img
                      src="download.jpeg"
                      alt="Profile Icon"
                      className="w-full h-full object-cover"
                    />
                  </div>
                </div>
                <div className="text-center">
                  <p className="text-lg font-medium"><strong>ID:</strong> {profile.id}</p>
                  <p className="text-lg font-medium"><strong>Username:</strong> {profile.username}</p>
                  <p className="text-lg font-medium"><strong>Email:</strong> {profile.email}</p>
                  <p className="text-lg font-medium"><strong>Role:</strong> {profile.role}</p>
                </div>
              </div>
            )}
          </section>
        )}

        {/* Removed comments section as per request */}
      </main>
</div>
  );
}

export default AgentDashboard;
