import React, { useState, useEffect } from 'react';

// import React, { useState, useEffect } from 'react';

function AdminDashboard({ token, userId }) {
  const [users, setUsers] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [comments, setComments] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [selectedAgent, setSelectedAgent] = useState(null);

  useEffect(() => {
    if (!token) return;

    const authHeader = `Bearer ${token}`;

    // Fetch users
    fetch('http://localhost:8080/api/users', {
      headers: {
        'Authorization': authHeader,
      },
    })
      .then(res => res.json())
      .then(data => setUsers(data))
      .catch(err => console.error('Error fetching users:', err));

    // Fetch tickets
    fetch('http://localhost:8080/api/tickets', {
      headers: {
        'Authorization': authHeader,
      },
    })
      .then(res => res.json())
      .then(data => setTickets(data))
      .catch(err => console.error('Error fetching tickets:', err));

    // Fetch comments
    fetch('http://localhost:8080/api/comments', {
      headers: {
        'Authorization': authHeader,
      },
    })
      .then(res => res.json())
      .then(data => setComments(data))
      .catch(err => console.error('Error fetching comments:', err));
  }, [token]);

  // Handlers
  const deleteUser = (id) => {
    const authHeader = `Bearer ${token}`;
    fetch(`http://localhost:8080/api/users/${id}`, {
      method: 'DELETE',
      headers: {
        'Authorization': authHeader,
      },
    })
      .then(() => setUsers(users.filter(user => user.id !== id)))
      .catch(err => console.error('Error deleting user:', err));
  };

  const editUser = (id) => {
    const newName = prompt('Enter new name:');
    if (newName) {
      const userToUpdate = users.find(user => user.id === id);
      if (!userToUpdate) return;
      const updatedUser = { ...userToUpdate, username: newName };
      const authHeader = `Bearer ${token}`;
      fetch(`http://localhost:8080/api/users/${id}`, {
        method: 'PUT',
        headers: {
          'Authorization': authHeader,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedUser),
      })
        .then(res => {
          if (res.ok) {
            setUsers(users.map(user => user.id === id ? updatedUser : user));
          } else {
            console.error('Failed to update user');
          }
        })
        .catch(err => console.error('Error updating user:', err));
    }
  };

  const deleteComment = (id) => {
    const authHeader = `Bearer ${token}`;
    fetch(`http://localhost:8080/api/comments/${id}`, {
      method: 'DELETE',
      headers: {
        'Authorization': authHeader,
      },
    })
      .then(() => setComments(comments.filter(comment => comment.id !== id)))
      .catch(err => console.error('Error deleting comment:', err));
  };

  const assignTicket = () => {
    if (selectedTicket && selectedAgent) {
      const authHeader = `Bearer ${token}`;
      fetch(`http://localhost:8080/api/tickets/${selectedTicket}/assign/${selectedAgent}`, {
        method: 'PUT',
        headers: {
          'Authorization': authHeader,
        },
      })
        .then(res => {
          if (res.ok) {
            // Update tickets state by refetching or updating locally
            setTickets(tickets.map(ticket =>
              ticket.id === parseInt(selectedTicket) ? { ...ticket, assignedAgent: { id: parseInt(selectedAgent) } } : ticket
            ));
            setSelectedTicket(null);
            setSelectedAgent(null);
          } else {
            console.error('Failed to update ticket');
          }
        })
        .catch(err => console.error('Error updating ticket:', err));
    }
  };

  const getUserNameById = (id) => {
    const user = users.find(u => u.id === id);
    return user ? user.username : 'Unassigned';
  };

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

      {/* Users Section */}
      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Users</h2>
        <table className="min-w-full bg-white border border-gray-300 rounded">
          <thead>
            <tr>
              <th className="border px-4 py-2 text-left">ID</th>
              <th className="border px-4 py-2 text-left">Name</th>
              <th className="border px-4 py-2 text-left">Role</th>
              <th className="border px-4 py-2 text-left">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id} className="hover:bg-gray-100">
                <td className="border px-4 py-2">{user.id}</td>
                <td className="border px-4 py-2">{user.username}</td>
                <td className="border px-4 py-2 capitalize">{user.role}</td>
                <td className="border px-4 py-2 space-x-2">
                  <button
                    onClick={() => editUser(user.id)}
                    className="text-blue-600 hover:underline"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => deleteUser(user.id)}
                    className="text-red-600 hover:underline"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* Tickets Section */}
      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Tickets (Total: {tickets.length})</h2>
        <div className="mb-4">
          <label htmlFor="ticketSelect" className="block mb-1 font-medium">Select Ticket</label>
          <select
            id="ticketSelect"
            value={selectedTicket || ''}
            onChange={(e) => setSelectedTicket(e.target.value)}
            className="border border-gray-300 rounded px-3 py-2 w-full max-w-xs"
          >
            <option value="" disabled>Select a ticket</option>
            {tickets.map(ticket => (
              <option key={ticket.id} value={ticket.id}>
                {ticket.title} (Assigned to: {getUserNameById(ticket.assignedAgent ? ticket.assignedAgent.id : null)})
              </option>
            ))}
          </select>
        </div>
        <div className="mb-4">
          <label htmlFor="agentSelect" className="block mb-1 font-medium">Assign to Agent</label>
          <select
            id="agentSelect"
            value={selectedAgent || ''}
            onChange={(e) => setSelectedAgent(e.target.value)}
            className="border border-gray-300 rounded px-3 py-2 w-full max-w-xs"
          >
            <option value="" disabled>Select an agent</option>
            {users.filter(user => user.role === 'agent').map(agent => (
              <option key={agent.id} value={agent.id}>{agent.username}</option>
            ))}
          </select>
        </div>
        <button
          onClick={assignTicket}
          disabled={!selectedTicket || !selectedAgent}
          className="bg-blue-600 text-white px-4 py-2 rounded disabled:opacity-50"
        >
          Assign Ticket
        </button>
      </section>

      {/* Comments Section */}
      <section>
        <h2 className="text-2xl font-semibold mb-4">Comments</h2>
        <ul className="list-disc pl-5 space-y-2">
          {comments.map(comment => (
            <li key={comment.id} className="flex justify-between items-center">
              <span>{comment.text}</span>
              <button
                onClick={() => deleteComment(comment.id)}
                className="text-red-600 hover:underline"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}

export default AdminDashboard;
