import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

function ResetPasswordOtpVerify() {
  const [otp, setOtp] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  // Extract email from query params
  const params = new URLSearchParams(location.search);
  const email = params.get('email');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setError('');
    try {
      const response = await fetch('http://localhost:8080/api/auth/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, otp }),
      });
      if (response.ok) {
        setMessage('OTP verified successfully. Please set your new password.');
        // Redirect to password reset form after short delay
        setTimeout(() => {
          navigate(`/reset-password?email=${encodeURIComponent(email)}`);
        }, 2000);
      } else {
        const errorText = await response.text();
        setError('OTP verification failed: ' + errorText);
      }
    } catch (err) {
      setError('OTP verification failed: ' + err.message);
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Verify OTP</h2>
      {message && <p className="mb-4 text-green-600">{message}</p>}
      {error && <p className="mb-4 text-red-600">{error}</p>}
      {!message && (
        <form onSubmit={handleSubmit}>
          <label className="block mb-2 font-semibold">Enter OTP</label>
          <input
            type="text"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            className="w-full border border-gray-300 rounded px-3 py-2 mb-4"
            required
          />
          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
          >
            Verify OTP
          </button>
        </form>
      )}
    </div>
  );
}

export default ResetPasswordOtpVerify;
