import React, { useState } from 'react';
import AgentDashboard from './AgentDashboard';
import AdminDashboard from './AdminDashboard';
import CustomerDashboard from './CustomerDashboard';
import About from './About';
import Contact from './Contact';

function App() {
  // ✅ Updated from username to email
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const [userRole, setUserRole] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showCreateAccount, setShowCreateAccount] = useState(false);
  const [currentPage, setCurrentPage] = useState('home');

  const [newUsername, setNewUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [newUserRole, setNewUserRole] = useState('CUSTOMER');
  const [newEmail, setNewEmail] = useState('');

  const [token, setToken] = useState(null);
  const [userId, setUserId] = useState(null);

  const [adminEmail, setAdminEmail] = useState('');
  const [adminPassword, setAdminPassword] = useState('');
  const [isAdminLoggedIn, setIsAdminLoggedIn] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }), // ✅ Updated field
      });

      if (response.ok) {
        const data = await response.json();
        setUserRole(data.role.toLowerCase());
        setToken(data.token);
        setUserId(data.userId);
        setIsLoggedIn(true);
        setPassword('');
      } else {
        alert('Login failed: Invalid credentials');
      }
    } catch (error) {
      alert('Login failed: ' + error.message);
    }
  };

  const handleAdminLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/api/admin/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email: adminEmail, password: adminPassword }),
      });

      if (response.ok) {
        const data = await response.json();
        setUserRole('admin');
        setToken(data.token);
        setUserId(data.userId);
        setIsAdminLoggedIn(true);
        setAdminPassword('');
      } else {
        alert('Admin login failed: Invalid credentials');
      }
    } catch (error) {
      alert('Admin login failed: ' + error.message);
    }
  };

  const handleLogout = () => {
    setEmail('');
    setPassword('');
    setUserRole(null);
    setIsLoggedIn(false);
    setShowCreateAccount(false);
    setAdminEmail('');
    setAdminPassword('');
    setIsAdminLoggedIn(false);
  };

  const handleCreateAccount = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8080/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: newUsername,
          email: newEmail,
          password: newPassword,
          role: newUserRole,
        }),
      });
      if (response.ok) {
        alert('Account created successfully');
        setNewUsername('');
        setNewPassword('');
        setNewUserRole('CUSTOMER');
        setNewEmail('');
        setShowCreateAccount(false);
      } else {
        const errorText = await response.text();
        alert('Account creation failed: ' + errorText);
      }
    } catch (error) {
      alert('Account creation failed: ' + error.message);
    }
  };

  if (isLoggedIn) {
    let DashboardComponent = null;
    if (userRole === 'agent') {
      DashboardComponent = AgentDashboard;
    } else if (userRole === 'admin') {
      DashboardComponent = AdminDashboard;
    } else if (userRole === 'customer') {
      DashboardComponent = CustomerDashboard;
    }

    return (
      <div className="min-h-screen bg-gray-100 flex flex-col">
        <nav className="bg-blue-600 text-white px-6 py-4 flex justify-between items-center">
          <div className="text-2xl font-bold">Support System</div>
          <button
            onClick={handleLogout}
            className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded transition"
          >
            Logout
          </button>
        </nav>
        <main className="flex-grow">
          <DashboardComponent token={token} userId={userId} />
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <nav className="bg-blue-600 text-white px-6 py-4 flex justify-between items-center">
        <div className="text-2xl font-bold">Support System</div>
        <div className="space-x-6">
          <button onClick={() => { setShowCreateAccount(false); setCurrentPage('home'); }}>Home</button>
          <button onClick={() => setCurrentPage('about')}>About</button>
          <button onClick={() => setCurrentPage('contact')}>Contact</button>
          <button onClick={() => { setShowCreateAccount(false); setCurrentPage('home'); }}>Login</button>
          <button onClick={() => { setShowCreateAccount(false); setCurrentPage('adminLogin'); }}>Admin Login</button>
        </div>
      </nav>

      <main className="flex-grow flex items-center justify-center">
        {currentPage === 'home' && !showCreateAccount && (
          <form
            onSubmit={handleLogin}
            className="bg-white p-8 rounded shadow-md w-full max-w-md"
          >
            <h2 className="text-2xl font-semibold mb-6 text-center">Login</h2>

            <div className="mb-4">
              <label htmlFor="email" className="block mb-1 font-medium">
                Email
              </label>
              <input
                id="email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div className="mb-6">
              <label htmlFor="password" className="block mb-1 font-medium">
                Password
              </label>
              <input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
            >
              Login
            </button>
            <p className="mt-4 text-center text-sm text-gray-600">
              New user?{' '}
              <button
                type="button"
                onClick={() => setShowCreateAccount(true)}
                className="text-blue-600 hover:underline"
              >
                Create account
              </button>
            </p>
          </form>
        )}

        {currentPage === 'adminLogin' && (
          <form
            onSubmit={handleAdminLogin}
            className="bg-white p-8 rounded shadow-md w-full max-w-md"
          >
            <h2 className="text-2xl font-semibold mb-6 text-center">Admin Login</h2>

            <div className="mb-4">
              <label htmlFor="adminEmail" className="block mb-1 font-medium">
                Email
              </label>
              <input
                id="adminEmail"
                type="email"
                value={adminEmail}
                onChange={(e) => setAdminEmail(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div className="mb-6">
              <label htmlFor="adminPassword" className="block mb-1 font-medium">
                Password
              </label>
              <input
                id="adminPassword"
                type="password"
                value={adminPassword}
                onChange={(e) => setAdminPassword(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
            >
              Login as Admin
            </button>
            <p className="mt-4 text-center text-sm text-gray-600">
              Not an admin?{' '}
              <button
                type="button"
                onClick={() => setCurrentPage('home')}
                className="text-blue-600 hover:underline"
              >
                User Login
              </button>
            </p>
          </form>
        )}

        {/* ... Account creation form already uses email, so no changes needed */}
        {currentPage === 'home' && showCreateAccount && (
          <form
            onSubmit={handleCreateAccount}
            className="bg-white p-8 rounded shadow-md w-full max-w-md"
          >
            <h2 className="text-2xl font-semibold mb-6 text-center">Create Account</h2>

            <div className="mb-4">
              <label htmlFor="newUsername" className="block mb-1 font-medium">
                Username
              </label>
              <input
                id="newUsername"
                type="text"
                value={newUsername}
                onChange={(e) => setNewUsername(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
                minLength={3}
                maxLength={20}
              />
            </div>

            <div className="mb-4">
              <label htmlFor="newEmail" className="block mb-1 font-medium">
                Email
              </label>
              <input
                id="newEmail"
                type="email"
                value={newEmail}
                onChange={(e) => setNewEmail(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div className="mb-4">
              <label htmlFor="newPassword" className="block mb-1 font-medium">
                Password
              </label>
              <input
                id="newPassword"
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
                minLength={6}
              />
            </div>

            <div className="mb-6">
              <label htmlFor="newUserRole" className="block mb-1 font-medium">
                Role
              </label>
              <select
                id="newUserRole"
                value={newUserRole}
                onChange={(e) => setNewUserRole(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="CUSTOMER">Customer</option>
                <option value="AGENT">Agent</option>
                {/* <option value="ADMIN">Admin</option> */}
              </select>
            </div>

            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
            >
              Create Account
            </button>

            <p className="mt-4 text-center text-sm text-gray-600">
              Already have an account?{' '}
              <button
                type="button"
                onClick={() => setShowCreateAccount(false)}
                className="text-blue-600 hover:underline"
              >
                Login
              </button>
            </p>
          </form>
        )}

        {currentPage === 'about' && (
          <About />
        )}

        {currentPage === 'contact' && (
          <Contact />
        )}
      </main>

      {/* About & Contact sections unchanged */}

    </div>
  );
}

export default App;
