import React, { useState, useEffect } from 'react';
import AdminSidebar from './AdminSidebar';

function AdminDashboard({ token, userId }) {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [users, setUsers] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [openTickets, setOpenTickets] = useState([]);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [editCommentId, setEditCommentId] = useState(null);
  const [editCommentText, setEditCommentText] = useState('');
  const [selectedTicketAssign, setSelectedTicketAssign] = useState(null);
  const [selectedAgent, setSelectedAgent] = useState(null);
  const [availableAgents, setAvailableAgents] = useState([]);
  const [showTickets, setShowTickets] = useState(true);
  const [activeSection, setActiveSection] = useState('viewUsers');
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    console.log("useEffect triggered for activeSection:", activeSection);
    if (!token) return;
    if (activeSection === 'viewComments') {
      console.log("Fetching comments for admin view");
      setLoading(true);
      const authHeader = `Bearer ${token}`;
      // Make a GET request to the comments API endpoint with the authorization header
      // This fetches all comments for the admin view
      fetch('http://localhost:8080/api/comments/getcomments', {
        headers: { Authorization: authHeader }
      })
        // Check if the response is successful (status code 200-299)
        .then(res => {
          if (!res.ok) throw new Error('Failed to fetch comments');
          // Parse the response body as JSON
          return res.json();
        })
        .then(data => {
          // Update the comments state with the fetched data
          setComments(data);
          // Clear any previous error messages
          setError(null);
        })
        // Catch and handle any errors during the fetch or processing
        .catch(err => setError(err.message))
        // Finally, set loading to false to indicate fetch completion
        .finally(() => setLoading(false));
    } else {
      setLoading(true);
      const authHeader = `Bearer ${token}`;

      // Fetch users
      fetch('http://localhost:8080/api/users', {
        headers: { Authorization: authHeader },
      })
        .then(res => {
          if (!res.ok) throw new Error('Failed to fetch users');
          return res.json();
        })
        .then(data => {
          setUsers(data);
          setError(null);
        })
        .catch(err => setError(err.message))
        .finally(() => setLoading(false));

      // Fetch tickets
      // Make a GET request to the tickets API endpoint with the authorization header
      // This fetches all tickets for the admin dashboard
      fetch('http://localhost:8080/api/tickets/gettickets', {
        headers: { Authorization: authHeader },
      })
        // Check if the response is successful (status code 200-299)
        .then(res => {
          if (!res.ok) throw new Error('Failed to fetch tickets');
          // Parse the response body as JSON
          return res.json();
        })
        .then(data => {
          // Update the tickets state with the fetched data
          setTickets(data);
          // Filter tickets with state 'OPEN' and update openTickets state
          setOpenTickets(data.filter(ticket => ticket.state === 'OPEN'));
          // Clear any previous error messages
          setError(null);
        })
        // Catch and handle any errors during the fetch or processing
        .catch(err => setError(err.message))
        // Finally, set loading to false to indicate fetch completion
        .finally(() => setLoading(false));
    }
  }, [token, activeSection]);

  useEffect(() => {
    if (activeSection === 'viewProfile' && userId && token) {
      setLoading(true);
      fetch(`http://localhost:8080/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then(res => {
          if (!res.ok) throw new Error('Failed to fetch profile');
          return res.json();
        })
        .then(data => {
          setProfile(data);
          setError(null);
        })
        .catch(err => setError(err.message))
        .finally(() => setLoading(false));
    }
  }, [activeSection, userId, token]);

  // Fetch all agents
  useEffect(() => {
    if (!token) return;
    const authHeader = `Bearer ${token}`;
    fetch('http://localhost:8080/api/users/agents', {
      headers: { Authorization: authHeader },
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch agents');
        return res.json();
      })
      .then(data => {
        setAvailableAgents(data);
        setError(null);
      })
      .catch(err => setError(err.message));
  }, [token]);

  // Add a comment
  const handleAddComment = () => {
    if (newComment.trim() && selectedTicketAssign) {
      fetch('http://localhost:8080/api/comments', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content: newComment,
          ticket: { id: selectedTicketAssign },
        }),
      })
        .then(res => {
          if (!res.ok) throw new Error('Failed to add comment');
          return res.json();
        })
        .then(newCommentData => {
          setComments(prev => [...prev, newCommentData]);
          setNewComment('');
          setError(null);
        })
        .catch(err => setError(err.message));
    }
  };

  // Edit comment preparation
  const handleEditComment = (commentId, text) => {
    setEditCommentId(commentId);
    setEditCommentText(text);
  };

  // Update comment
  const handleUpdateComment = () => {
    if (editCommentText.trim() && editCommentId !== null) {
      fetch(`http://localhost:8080/api/comments/${editCommentId}`, {
        method: 'PUT',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ content: editCommentText }),
      })
        .then(res => {
          if (!res.ok) throw new Error('Failed to update comment');
          return res.json();
        })
        .then(updatedComment => {
          setComments(prev =>
            prev.map(comment =>
              comment.id === editCommentId ? updatedComment : comment
            )
          );
          setEditCommentId(null);
          setEditCommentText('');
          setError(null);
        })
        .catch(err => setError(err.message));
    }
  };

  // Delete comment
  const deleteComment = (id) => {
    fetch(`http://localhost:8080/api/comments/${id}`, {
      method: 'DELETE',
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(() => setComments(comments.filter(comment => comment.id !== id)))
      .catch(err => setError(err.message));
  };

  // Assign ticket to agent
  const assignTicket = () => {
    if (selectedTicketAssign && selectedAgent) {
      fetch(`http://localhost:8080/api/tickets/${selectedTicketAssign}/assign/${selectedAgent}`, {
        method: 'PUT',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      })
        .then(res => {
          if (!res.ok) throw new Error('Failed to assign ticket');
          setTickets(prev =>
            prev.map(ticket =>
              ticket.id === parseInt(selectedTicketAssign)
                ? { ...ticket, assignedAgent: { id: parseInt(selectedAgent) } }
                : ticket
            )
          );
          setSelectedTicketAssign(null);
          setSelectedAgent(null);
          setError(null);
        })
        .catch(err => setError(err.message));
    }
  };

  return (
    <div className="flex min-h-screen font-poppins bg-gray-100">
      <AdminSidebar onSelect={setActiveSection} />
      <main className="flex-grow p-8 ml-64 max-w-6xl mx-auto">

        {error && <p className="text-red-600 mb-4">{error}</p>}

        {activeSection === 'viewUsers' && (
          <section className="mb-8">
            <h2 className="text-2xl font-semibold mb-4 text-blue-800">Users</h2>
            <table className="min-w-full bg-white border border-gray-300 rounded">
              <thead>
                <tr>
                  <th className="border px-4 py-2 text-left">ID</th>
                  <th className="border px-4 py-2 text-left">Username</th>
                  <th className="border px-4 py-2 text-left">Role</th>
                </tr>
              </thead>
              <tbody>
                {users.map(user => (
                  <tr key={user.id} className="hover:bg-gray-100">
                    <td className="border px-4 py-2">{user.id}</td>
                    <td className="border px-4 py-2">{user.username}</td>
                    <td className="border px-4 py-2 capitalize">{user.role}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </section>
        )}

        {activeSection === 'viewTickets' && (
          <>
            <section className="mb-8">
              <h2 className="text-2xl font-semibold mb-4 text-blue-800">Tickets (Total: {tickets.length})</h2>
              <table className="min-w-full bg-white border border-gray-300 rounded">
              <thead>
                <tr>
                  <th className="border px-4 py-2 text-left">ID</th>
                  <th className="border px-4 py-2 text-left">Title</th>
                  <th className="border px-4 py-2 text-left">Description</th>
                  <th className="border px-4 py-2 text-left">Status</th>
                  <th className="border px-4 py-2 text-left">Created By ID</th>
                  <th className="border px-4 py-2 text-left">Agent ID</th>
                </tr>
              </thead>
              <tbody>
                {tickets.map(ticket => (
                  <tr key={ticket.id} className="hover:bg-gray-100">
                    <td className="border px-4 py-2">{ticket.id}</td>
                    <td className="border px-4 py-2">{ticket.title}</td>
                    <td className="border px-4 py-2">{ticket.description}</td>
                    <td className="border px-4 py-2">{ticket.state}</td>
                    <td className="border px-4 py-2">{ticket.createdBy ? ticket.createdBy.id : 'null'}</td>
                    <td className="border px-4 py-2">
                      {ticket.assignedAgent
                        ? ticket.assignedAgent.id
                        : ticket.state === 'ASSIGNED'
                        ? 'Assigned'
                        : 'Unassigned'}
                    </td>
                  </tr>
                ))}
              </tbody>
              </table>
            </section>
          </>
        )}

        {activeSection === 'assignTickets' && (
          <section className="mb-8">
            <h2 className="text-2xl font-semibold mb-4 text-blue-800">Assign Tickets</h2>
            <div className="mb-4">
              <label htmlFor="ticketSelect" className="block mb-1 font-medium">Select Ticket</label>
              <select
                id="ticketSelect"
                value={selectedTicketAssign || ''}
                onChange={(e) => setSelectedTicketAssign(e.target.value)}
                className="border border-gray-300 rounded px-3 py-2 w-full max-w-xs"
              >
                <option value="" disabled>Select a ticket</option>
                {openTickets.map(ticket => (
                  <option key={ticket.id} value={ticket.id}>
                    {ticket.title} (Assigned to: {ticket.assignedAgent ? ticket.assignedAgent.username : 'Unassigned'})
                  </option>
                ))}
              </select>
            </div>
            <div className="mb-4">
              <label htmlFor="agentSelect" className="block mb-1 font-medium">Assign to Agent</label>
              <select
                id="agentSelect"
                value={selectedAgent || ''}
                onChange={(e) => setSelectedAgent(e.target.value)}
                className="border border-gray-300 rounded px-3 py-2 w-full max-w-xs"
              >
                <option value="" disabled>Select an agent</option>
                {availableAgents.map(agent => (
                  <option key={agent.id} value={agent.id}>{agent.username}</option>
                ))}
              </select>
            </div>
            <button
              onClick={assignTicket}
              disabled={!selectedTicketAssign || !selectedAgent}
              className="bg-blue-600 text-white px-4 py-2 rounded disabled:opacity-50"
            >
              Assign Ticket
            </button>
          </section>
        )}

        {activeSection === 'viewProfile' && (
          <section className="mb-8 flex flex-col items-center justify-center">
            <h2 className="text-3xl font-semibold mb-6 text-blue-800">Profile</h2>
            {loading && <p>Loading profile...</p>}
            {error && <p className="text-red-600">{error}</p>}
            {profile && (
              <div className="flex flex-col items-center bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
                <div className="mb-6">
                <div className="w-24 h-24 rounded-full bg-blue-600 flex items-center justify-center shadow-lg mx-auto overflow-hidden">
                  <img
                    src="download.jpeg"
                    alt="Profile Icon"
                    className="w-full h-full object-cover"
                  />
                </div>
                </div>
                <div className="text-center">
                  <p className="text-lg font-medium"><strong>ID:</strong> {profile.id}</p>
                  <p className="text-lg font-medium"><strong>Username:</strong> {profile.username}</p>
                  <p className="text-lg font-medium"><strong>Email:</strong> {profile.email}</p>
                  <p className="text-lg font-medium"><strong>Role:</strong> {profile.role}</p>
                </div>
              </div>
            )}
          </section>
        )}

        {activeSection === 'viewComments' && (
          <section className="mb-8">
            <h2 className="text-2xl font-semibold mb-4 text-blue-800">Comments</h2>
            <table className="min-w-full bg-white border border-gray-300 rounded">
              <thead>
                <tr>
                  <th className="border px-4 py-2 text-left">ID</th>
                  <th className="border px-4 py-2 text-left">Content</th>
                  <th className="border px-4 py-2 text-left">Ticket ID</th>
                  <th className="border px-4 py-2 text-left">User ID</th>
                  <th className="border px-4 py-2 text-left">Actions</th>
                </tr>
              </thead>
              <tbody>
                {comments.map(comment => (
                  <tr key={comment.id} className="hover:bg-gray-100">
                    <td className="border px-4 py-2">{comment.id}</td>
                    <td className="border px-4 py-2">{comment.content}</td>
                    <td className="border px-4 py-2">{comment.ticket ? comment.ticket.id : 'N/A'}</td>
                    <td className="border px-4 py-2">{comment.user ? comment.user.id : 'N/A'}</td>
                    <td className="border px-4 py-2">
                      <button
                        onClick={() => handleEditComment(comment.id, comment.content)}
                        className="bg-yellow-400 text-white px-2 py-1 rounded mr-2"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => deleteComment(comment.id)}
                        className="bg-red-600 text-white px-2 py-1 rounded"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {editCommentId !== null && (
              <div className="mt-4">
                <textarea
                  value={editCommentText}
                  onChange={(e) => setEditCommentText(e.target.value)}
                  className="w-full border border-gray-300 rounded p-2"
                  rows={4}
                />
                <button
                  onClick={handleUpdateComment}
                  className="bg-blue-600 text-white px-4 py-2 rounded mt-2"
                >
                  Update Comment
                </button>
                <button
                  onClick={() => setEditCommentId(null)}
                  className="ml-2 bg-gray-400 text-white px-4 py-2 rounded mt-2"
                >
                  Cancel
                </button>
              </div>
            )}
          </section>
        )}
      </main>
    </div>
  );
}

export default AdminDashboard;
