import React, { useState, useEffect } from 'react';

function CustomerDashboard({ token }) {
  const [customerId, setCustomerId] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [newTicketTitle, setNewTicketTitle] = useState('');
  const [selectedTicketId, setSelectedTicketId] = useState(null);
  const [newComment, setNewComment] = useState('');
  const [editCommentId, setEditCommentId] = useState(null);
  const [editCommentText, setEditCommentText] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch current user info
  useEffect(() => {
    if (!token) return;

    setLoading(true);
    fetch('http://localhost:8080/api/users/me', {
      headers: {
        'Authorization': `Bearer ${token}`,
      },
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch user info');
        return res.json();
      })
      .then(user => {
        setCustomerId(user.id);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching current user:', err);
        setError(err.message);
        setLoading(false);
      });
  }, [token]);

  // Fetch tickets created by customer
  useEffect(() => {
    if (!token || !customerId) return;

    setLoading(true);
    fetch('http://localhost:8080/api/tickets/customer', {  // Note: no /${customerId}
      headers: {
        'Authorization': `Bearer ${token}`,
      },
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch tickets');
        return res.json();
      })
      .then(data => {
        setTickets(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching tickets:', err);
        setError(err.message);
        setLoading(false);
      });
  }, [token, customerId]);

  const handleCreateTicket = () => {
    if (newTicketTitle.trim()) {
      fetch('http://localhost:8080/api/tickets', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          title: newTicketTitle,
          description: '',  // Optional: you can add a description input if needed
          state: 'OPEN',    // Must be uppercase to match enum
        }),
      })
        .then(res => {
          if (!res.ok) throw new Error('Failed to create ticket');
          return res.json();
        })
        .then(newTicket => {
          setTickets([...tickets, newTicket]);
          setNewTicketTitle('');
          setError(null);
        })
        .catch(err => {
          console.error('Error creating ticket:', err);
          setError(err.message);
        });
    }
  };

  const handleAddComment = () => {
    if (newComment.trim() && selectedTicketId) {
      fetch('http://localhost:8080/api/comments', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content: newComment,
          ticket: { id: selectedTicketId },
        }),
      })
        .then(res => {
          if (!res.ok) throw new Error('Failed to add comment');
          return res.json();
        })
        .then(newCommentData => {
          setTickets(tickets.map(ticket => {
            if (ticket.id === selectedTicketId) {
              return {
                ...ticket,
                comments: [...(ticket.comments || []), newCommentData],
              };
            }
            return ticket;
          }));
          setNewComment('');
          setError(null);
        })
        .catch(err => {
          console.error('Error adding comment:', err);
          setError(err.message);
        });
    }
  };

  const handleEditComment = (ticketId, commentId, text) => {
    setSelectedTicketId(ticketId);
    setEditCommentId(commentId);
    setEditCommentText(text);
  };

  const handleUpdateComment = () => {
    if (editCommentText.trim() && selectedTicketId && editCommentId !== null) {
      fetch(`http://localhost:8080/api/comments/${editCommentId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ content: editCommentText }),
      })
        .then(res => {
          if (res.ok) {
            setTickets(tickets.map(ticket => {
              if (ticket.id === selectedTicketId) {
                return {
                  ...ticket,
                  comments: ticket.comments.map(comment =>
                    comment.id === editCommentId ? { ...comment, content: editCommentText } : comment
                  ),
                };
              }
              return ticket;
            }));
            setEditCommentId(null);
            setEditCommentText('');
            setError(null);
          } else {
            throw new Error('Failed to update comment');
          }
        })
        .catch(err => {
          console.error('Error updating comment:', err);
          setError(err.message);
        });
    }
  };

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6">Customer Dashboard</h1>

      {loading && <p>Loading...</p>}
      {error && <p className="text-red-600 mb-4">{error}</p>}

      {/* Create Ticket */}
      <div className="mb-6">
        <h2 className="text-2xl font-semibold mb-2">Create Ticket</h2>
        <input
          type="text"
          placeholder="Ticket title"
          value={newTicketTitle}
          onChange={(e) => setNewTicketTitle(e.target.value)}
          className="border border-gray-300 rounded px-3 py-2 w-full max-w-md mb-2"
        />
        <button
          onClick={handleCreateTicket}
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Create
        </button>
      </div>

      {/* Tickets List */}
      <div className="space-y-6">
        {tickets.map(ticket => (
          <div key={ticket.id} className="border p-4 rounded shadow">
            <h2 className="text-xl font-semibold mb-2">{ticket.title}</h2>
            <p>Status: <strong>{ticket.state}</strong></p>

            <div className="mt-4">
              <h3 className="font-semibold mb-1">Comments:</h3>
              <ul className="list-disc pl-5 mb-2">
                {(ticket.comments || []).map(comment => (
                  <li key={comment.id} className="flex items-center justify-between">
                    {editCommentId === comment.id && selectedTicketId === ticket.id ? (
                      <>
                        <input
                          type="text"
                          value={editCommentText}
                          onChange={(e) => setEditCommentText(e.target.value)}
                          className="border border-gray-300 rounded px-2 py-1 mr-2 flex-grow"
                        />
                        <button
                          onClick={handleUpdateComment}
                          className="bg-green-600 text-white px-2 py-1 rounded"
                        >
                          Save
                        </button>
                      </>
                    ) : (
                      <>
                        <span>{comment.content}</span>
                        <button
                          onClick={() => handleEditComment(ticket.id, comment.id, comment.content)}
                          className="text-blue-600 hover:underline ml-2"
                        >
                          Edit
                        </button>
                      </>
                    )}
                  </li>
                ))}
              </ul>
              <div className="flex space-x-2">
                <input
                  type="text"
                  placeholder="Add a comment"
                  value={selectedTicketId === ticket.id ? newComment : ''}
                  onChange={(e) => {
                    setSelectedTicketId(ticket.id);
                    setNewComment(e.target.value);
                  }}
                  className="border border-gray-300 rounded px-2 py-1 flex-grow"
                />
                <button
                  onClick={handleAddComment}
                  className="bg-blue-600 text-white px-4 py-1 rounded"
                  disabled={selectedTicketId !== ticket.id}
                >
                  Add
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CustomerDashboard;
