import React, { useState } from 'react';
import AgentDashboard from './AgentDashboard';
import AdminDashboard from './AdminDashboard';
import CustomerDashboard from './CustomerDashboard';

function App() {
  // ✅ Updated from username to email
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const [userRole, setUserRole] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showCreateAccount, setShowCreateAccount] = useState(false);
  const [currentPage, setCurrentPage] = useState('home');

  const [newUsername, setNewUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [newUserRole, setNewUserRole] = useState('CUSTOMER');
  const [newEmail, setNewEmail] = useState('');

  const [token, setToken] = useState(null);

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }), // ✅ Updated field
      });

      if (response.ok) {
        const data = await response.json();
        setUserRole(data.role.toLowerCase());
        setToken(data.token);
        setIsLoggedIn(true);
        setPassword('');
      } else {
        alert('Login failed: Invalid credentials');
      }
    } catch (error) {
      alert('Login failed: ' + error.message);
    }
  };

  const handleLogout = () => {
    setEmail('');
    setPassword('');
    setUserRole(null);
    setIsLoggedIn(false);
    setShowCreateAccount(false);
  };

  const handleCreateAccount = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8080/api/users/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: newUsername,
          password: newPassword,
          role: newUserRole,
          email: newEmail,
        }),
      });
      if (response.ok) {
        alert('Account created successfully');
        setNewUsername('');
        setNewPassword('');
        setNewUserRole('CUSTOMER');
        setNewEmail('');
        setShowCreateAccount(false);
      } else {
        const errorText = await response.text();
        alert('Account creation failed: ' + errorText);
      }
    } catch (error) {
      alert('Account creation failed: ' + error.message);
    }
  };

  if (isLoggedIn) {
    let DashboardComponent = null;
    if (userRole === 'agent') {
      DashboardComponent = AgentDashboard;
    } else if (userRole === 'admin') {
      DashboardComponent = AdminDashboard;
    } else if (userRole === 'customer') {
      DashboardComponent = CustomerDashboard;
    }

    return (
      <div className="min-h-screen bg-gray-100 flex flex-col">
        <nav className="bg-blue-600 text-white px-6 py-4 flex justify-between items-center">
          <div className="text-2xl font-bold">Support System</div>
          <button
            onClick={handleLogout}
            className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded transition"
          >
            Logout
          </button>
        </nav>
        <main className="flex-grow">
          <DashboardComponent token={token} />
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <nav className="bg-blue-600 text-white px-6 py-4 flex justify-between items-center">
        <div className="text-2xl font-bold">Support System</div>
        <div className="space-x-6">
          <button onClick={() => { setShowCreateAccount(false); setCurrentPage('home'); }}>Home</button>
          <button onClick={() => setCurrentPage('about')}>About</button>
          <button onClick={() => setCurrentPage('contact')}>Contact</button>
          <button onClick={() => { setShowCreateAccount(false); setCurrentPage('home'); }}>Login</button>
        </div>
      </nav>

      <main className="flex-grow flex items-center justify-center">
        {currentPage === 'home' && !showCreateAccount && (
          <form
            onSubmit={handleLogin}
            className="bg-white p-8 rounded shadow-md w-full max-w-md"
          >
            <h2 className="text-2xl font-semibold mb-6 text-center">Login</h2>

            {/* ✅ Changed Username to Email */}
            <div className="mb-4">
              <label htmlFor="email" className="block mb-1 font-medium">
                Email
              </label>
              <input
                id="email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div className="mb-6">
              <label htmlFor="password" className="block mb-1 font-medium">
                Password
              </label>
              <input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
            >
              Login
            </button>
            <p className="mt-4 text-center text-sm text-gray-600">
              New user?{' '}
              <button
                type="button"
                onClick={() => setShowCreateAccount(true)}
                className="text-blue-600 hover:underline"
              >
                Create account
              </button>
            </p>
          </form>
        )}

        {/* ... Account creation form already uses email, so no changes needed */}
        {currentPage === 'home' && showCreateAccount && (
          /* Keep your existing Create Account form */
          <>{/* Existing code from your original post */}</>
        )}
      </main>

      {/* About & Contact sections unchanged */}
    </div>
  );
}

export default App;
