 // src/components/VerifyOtp.jsx

import React, { useState } from "react";
import axios from "axios";

function VerifyOtp({ email, onOtpVerified }) {
  const [otp, setOtp] = useState("");
  const [message, setMessage] = useState("");

  const handleVerify = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:8080/api/auth/verify-otp", { email, otp });
      setMessage(res.data.message || "OTP verified.");
      onOtpVerified(otp);
    } catch (error) {
      setMessage(error.response?.data?.message || "Invalid or expired OTP.");
    }
  };

  return (
    <div className="card">
      <h2>Verify OTP</h2>
      <form onSubmit={handleVerify}>
        <input
          type="text"
          placeholder="Enter OTP"
          value={otp}
          onChange={(e) => setOtp(e.target.value)}
          required
        />
        <button type="submit">Verify</button>
      </form>
      <p>{message}</p>
    </div>
  );
}

export default VerifyOtp;
