import React, { useState, useEffect } from 'react';
import AgentSidebar from './AgentSidebar';

function AgentDashboard({ token, userId }) {
  const [agentId, setAgentId] = useState(userId);
  const [tickets, setTickets] = useState([]);
  const [selectedTicketId, setSelectedTicketId] = useState(null);
  const [newComment, setNewComment] = useState('');
  const [editCommentId, setEditCommentId] = useState(null);
  const [editCommentText, setEditCommentText] = useState('');
  const [activeView, setActiveView] = useState('view');
  const [users, setUsers] = useState([]);

  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [passwordLoading, setPasswordLoading] = useState(false);
  const [passwordError, setPasswordError] = useState('');
  const [passwordSuccess, setPasswordSuccess] = useState('');

  useEffect(() => {
    if (!token || !agentId) return;

    setLoading(true);
    setError(null);

    // Fetch tickets assigned to this agent
    fetch(`http://localhost:8080/api/tickets`, {
      headers: {
        'Authorization': `Bearer ${token}`,
      },
    })
      .then(res => res.json())
      .then(data => {
        const assignedTickets = data.filter(ticket => ticket.assignedAgent && ticket.assignedAgent.id === agentId);
        setTickets(assignedTickets);
      })
      .catch(err => {
        console.error('Error fetching tickets:', err);
        setError('Failed to fetch tickets');
      })
      .finally(() => setLoading(false));
  }, [token, agentId]);

  useEffect(() => {
    if (activeView === 'view-users' && token) {
      fetch('http://localhost:8080/api/users', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
        .then(res => res.json())
        .then(data => setUsers(data))
        .catch(err => console.error('Error fetching users:', err));
    }
  }, [activeView, token]);

  useEffect(() => {
    if (activeView === 'profile' && token && agentId) {
      setLoading(true);
      setError(null);
      fetch(`http://localhost:8080/api/users/${agentId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
        .then(res => {
          if (!res.ok) throw new Error('Failed to fetch profile');
          return res.json();
        })
        .then(data => {
          setProfile(data);
          setError(null);
        })
        .catch(err => {
          console.error('Error fetching profile:', err);
          setError('Failed to fetch profile');
        })
        .finally(() => setLoading(false));
    }
  }, [activeView, token, agentId]);

  const handleChangePassword = (e) => {
    e.preventDefault();
    setPasswordLoading(true);
    setPasswordError('');
    setPasswordSuccess('');

    fetch(`http://localhost:8080/api/users/${agentId}/change-password`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        currentPassword,
        newPassword,
      }),
    })
      .then(res => {
        if (!res.ok) {
          return res.text().then(text => { throw new Error(text || 'Failed to change password'); });
        }
        return res.json();
      })
      .then(() => {
        setPasswordSuccess('Password changed successfully');
        setCurrentPassword('');
        setNewPassword('');
      })
      .catch(err => {
        setPasswordError(err.message);
      })
      .finally(() => {
        setPasswordLoading(false);
      });
  };

  const handleAddComment = () => {
    if (newComment.trim() && selectedTicketId) {
      // Post new comment to backend
      fetch('http://localhost:8080/api/comments', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
          body: JSON.stringify({
          ticketId: selectedTicketId,
          content: newComment,
        }),
      })
        .then(res => res.json())
        .then(newCommentData => {
          setTickets(tickets.map(ticket => {
            if (ticket.id === selectedTicketId) {
              return {
                ...ticket,
                comments: [...(ticket.comments || []), newCommentData],
              };
            }
            return ticket;
          }));
          setNewComment('');
        })
        .catch(err => console.error('Error adding comment:', err));
    }
  };

  const handleEditComment = (ticketId, commentId, text) => {
    setSelectedTicketId(ticketId);
    setEditCommentId(commentId);
    setEditCommentText(text);
  };

  const handleUpdateComment = () => {
    if (editCommentText.trim() && selectedTicketId && editCommentId !== null) {
      // Update comment in backend
      fetch(`http://localhost:8080/api/comments/${editCommentId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ content: editCommentText }),
      })
        .then(res => {
          if (res.ok) {
            setTickets(tickets.map(ticket => {
              if (ticket.id === selectedTicketId) {
                return {
                  ...ticket,
                  comments: ticket.comments.map(comment =>
                    comment.id === editCommentId ? { ...comment, content: editCommentText } : comment
                  ),
                };
              }
              return ticket;
            }));
            setEditCommentId(null);
            setEditCommentText('');
          } else {
            console.error('Failed to update comment');
          }
        })
        .catch(err => console.error('Error updating comment:', err));
    }
  };

  const handleChangeTicketState = (ticketId, newState) => {
    // Update ticket state in backend
    fetch(`http://localhost:8080/api/tickets/${ticketId}`, {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ state: newState }),
    })
      .then(res => {
        if (res.ok) {
          setTickets(tickets.map(ticket =>
            ticket.id === ticketId ? { ...ticket, state: newState } : ticket
          ));
        } else {
          console.error('Failed to update ticket state');
        }
      })
      .catch(err => console.error('Error updating ticket state:', err));
  };

  return (
    <div className="flex">
      <AgentSidebar activeView={activeView} setActiveView={setActiveView} />
      <main className="p-8 flex-grow max-w-6xl mx-auto">
        {activeView === 'view' && (
          <>
            <h1 className="text-3xl font-bold mb-6">Agent Dashboard</h1>
            <p className="mb-4">Total Tickets Assigned: {tickets.length}</p>
            <div className="space-y-6">
              {tickets.map(ticket => (
                <div key={ticket.id} className="border p-4 rounded shadow">
                  <h2 className="text-xl font-semibold mb-2">{ticket.title}</h2>
                  <p className="mb-2">State: <strong>{ticket.state}</strong></p>
                  <div className="mb-2">
                    <label className="mr-2 font-medium">Change State:</label>
                    <select
                      value={ticket.state}
                      onChange={(e) => handleChangeTicketState(ticket.id, e.target.value)}
                      className="border border-gray-300 rounded px-2 py-1"
                    >
                      <option value="OPEN">Open</option>
                      <option value="ASSIGNED">Assigned</option>
                      <option value="SOLVED">Solved</option>
                    </select>
                  </div>

                  <div>
                    <h3 className="font-semibold mb-1">Comments:</h3>
                    <ul className="list-disc pl-5 mb-2">
                      {ticket.comments && ticket.comments.map(comment => (
                        <li key={comment.id} className="flex items-center justify-between">
                          {editCommentId === comment.id && selectedTicketId === ticket.id ? (
                            <>
                              <input
                                type="text"
                                value={editCommentText}
                                onChange={(e) => setEditCommentText(e.target.value)}
                                className="border border-gray-300 rounded px-2 py-1 mr-2 flex-grow"
                              />
                              <button
                                onClick={handleUpdateComment}
                                className="bg-green-600 text-white px-2 py-1 rounded"
                              >
                                Save
                              </button>
                            </>
                          ) : (
                            <>
                              <span>{comment.content}</span>
                              <button
                                onClick={() => handleEditComment(ticket.id, comment.id, comment.content)}
                                className="text-blue-600 hover:underline ml-2"
                              >
                                Edit
                              </button>
                            </>
                          )}
                        </li>
                      ))}
                    </ul>
                    <div className="flex space-x-2">
                      <input
                        type="text"
                        placeholder="Add a comment"
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        className="border border-gray-300 rounded px-2 py-1 flex-grow"
                      />
                      <button
                        onClick={handleAddComment}
                        className="bg-blue-600 text-white px-4 py-1 rounded"
                      >
                        Add
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}

        {activeView === 'view-users' && (
          <>
            <h2 className="text-2xl font-semibold mb-4">Users</h2>
            <ul className="list-disc pl-5">
              {users.map(user => (
                <li key={user.id}>
                  {user.username} - {user.email} - {user.role}
                </li>
              ))}
            </ul>
          </>
        )}

        {activeView === 'assign-tickets' && (
          <>
            <h2 className="text-2xl font-semibold mb-4">Assign Tickets</h2>
            {/* Implement assign tickets UI here */}
            <p>Feature to assign tickets will be implemented here.</p>
          </>
        )}

        {activeView === 'profile' && (
          <section className="mb-8 flex flex-col items-center justify-center">
            <h2 className="text-3xl font-semibold mb-6 text-blue-800">Profile</h2>
            {loading && <p>Loading profile...</p>}
            {error && <p className="text-red-600">{error}</p>}
            {profile && (
              <div className="flex flex-col items-center bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
                <div className="mb-6">
                  <div className="w-24 h-24 rounded-full bg-blue-600 flex items-center justify-center shadow-lg mx-auto overflow-hidden">
                    <img
                      src="download.jpeg"
                      alt="Profile Icon"
                      className="w-full h-full object-cover"
                    />
                  </div>
                </div>
                <div className="text-center">
                  <p className="text-lg font-medium"><strong>ID:</strong> {profile.id}</p>
                  <p className="text-lg font-medium"><strong>Username:</strong> {profile.username}</p>
                  <p className="text-lg font-medium"><strong>Email:</strong> {profile.email}</p>
                  <p className="text-lg font-medium"><strong>Role:</strong> {profile.role}</p>
                </div>
                <div className="mt-6 w-full max-w-md">
                  <h3 className="text-xl font-semibold mb-4">Change Password</h3>
                  <form onSubmit={handleChangePassword} className="space-y-4">
                    <div>
                      <label htmlFor="currentPassword" className="block mb-1 font-medium">Current Password</label>
                      <input
                        type="password"
                        id="currentPassword"
                        value={currentPassword}
                        onChange={(e) => setCurrentPassword(e.target.value)}
                        className="w-full border border-gray-300 rounded px-3 py-2"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="newPassword" className="block mb-1 font-medium">New Password</label>
                      <input
                        type="password"
                        id="newPassword"
                        value={newPassword}
                        onChange={(e) => setNewPassword(e.target.value)}
                        className="w-full border border-gray-300 rounded px-3 py-2"
                        required
                      />
                    </div>
                    <button
                      type="submit"
                      className="bg-blue-600 text-white px-4 py-2 rounded"
                      disabled={passwordLoading}
                    >
                      {passwordLoading ? 'Changing...' : 'Change Password'}
                    </button>
                    {passwordError && <p className="text-red-600 mt-2">{passwordError}</p>}
                    {passwordSuccess && <p className="text-green-600 mt-2">{passwordSuccess}</p>}
                  </form>
                </div>
              </div>
            )}
          </section>
        )}
      </main>
    </div>
  );
}

export default AgentDashboard;
