import React, { useState } from 'react';

function AgentDashboard() {
  // Simulated agent id
  const agentId = 1;

  // Simulated tickets data
  const [tickets, setTickets] = useState([
    { id: 101, title: 'Issue with login', state: 'open', assignedTo: 1, comments: [{ id: 1, text: 'Initial comment' }] },
    { id: 102, title: 'Page not loading', state: 'in progress', assignedTo: 1, comments: [] },
    { id: 103, title: 'Feature request', state: 'closed', assignedTo: 2, comments: [] },
  ]);

  const [selectedTicketId, setSelectedTicketId] = useState(null);
  const [newComment, setNewComment] = useState('');
  const [editCommentId, setEditCommentId] = useState(null);
  const [editCommentText, setEditCommentText] = useState('');

  // Filter tickets assigned to this agent
  const assignedTickets = tickets.filter(ticket => ticket.assignedTo === agentId);

  const handleAddComment = () => {
    if (newComment.trim() && selectedTicketId) {
      setTickets(tickets.map(ticket => {
        if (ticket.id === selectedTicketId) {
          const newId = ticket.comments.length > 0 ? Math.max(...ticket.comments.map(c => c.id)) + 1 : 1;
          return {
            ...ticket,
            comments: [...ticket.comments, { id: newId, text: newComment }]
          };
        }
        return ticket;
      }));
      setNewComment('');
    }
  };

  const handleEditComment = (ticketId, commentId, text) => {
    setSelectedTicketId(ticketId);
    setEditCommentId(commentId);
    setEditCommentText(text);
  };

  const handleUpdateComment = () => {
    if (editCommentText.trim() && selectedTicketId && editCommentId !== null) {
      setTickets(tickets.map(ticket => {
        if (ticket.id === selectedTicketId) {
          return {
            ...ticket,
            comments: ticket.comments.map(comment =>
              comment.id === editCommentId ? { ...comment, text: editCommentText } : comment
            )
          };
        }
        return ticket;
      }));
      setEditCommentId(null);
      setEditCommentText('');
    }
  };

  const handleChangeTicketState = (ticketId, newState) => {
    setTickets(tickets.map(ticket =>
      ticket.id === ticketId ? { ...ticket, state: newState } : ticket
    ));
  };

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6">Agent Dashboard</h1>
      <p className="mb-4">Total Tickets Assigned: {assignedTickets.length}</p>

      <div className="space-y-6">
        {assignedTickets.map(ticket => (
          <div key={ticket.id} className="border p-4 rounded shadow">
            <h2 className="text-xl font-semibold mb-2">{ticket.title}</h2>
            <p className="mb-2">State: <strong>{ticket.state}</strong></p>
            <div className="mb-2">
              <label className="mr-2 font-medium">Change State:</label>
              <select
                value={ticket.state}
                onChange={(e) => handleChangeTicketState(ticket.id, e.target.value)}
                className="border border-gray-300 rounded px-2 py-1"
              >
                <option value="open">Open</option>
                <option value="in progress">In Progress</option>
                <option value="closed">Closed</option>
              </select>
            </div>

            <div>
              <h3 className="font-semibold mb-1">Comments:</h3>
              <ul className="list-disc pl-5 mb-2">
                {ticket.comments.map(comment => (
                  <li key={comment.id} className="flex items-center justify-between">
                    {editCommentId === comment.id && selectedTicketId === ticket.id ? (
                      <>
                        <input
                          type="text"
                          value={editCommentText}
                          onChange={(e) => setEditCommentText(e.target.value)}
                          className="border border-gray-300 rounded px-2 py-1 mr-2 flex-grow"
                        />
                        <button
                          onClick={handleUpdateComment}
                          className="bg-green-600 text-white px-2 py-1 rounded"
                        >
                          Save
                        </button>
                      </>
                    ) : (
                      <>
                        <span>{comment.text}</span>
                        <button
                          onClick={() => handleEditComment(ticket.id, comment.id, comment.text)}
                          className="text-blue-600 hover:underline ml-2"
                        >
                          Edit
                        </button>
                      </>
                    )}
                  </li>
                ))}
              </ul>
              <div className="flex space-x-2">
                <input
                  type="text"
                  placeholder="Add a comment"
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  className="border border-gray-300 rounded px-2 py-1 flex-grow"
                />
                <button
                  onClick={handleAddComment}
                  className="bg-blue-600 text-white px-4 py-1 rounded"
                >
                  Add
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AgentDashboard;
