import React, { useState } from 'react';
import AgentDashboard from './AgentDashboard';
import AdminDashboard from './AdminDashboard';
import CustomerDashboard from './CustomerDashboard';

function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [userRole, setUserRole] = useState(null); // 'agent', 'admin', 'customer'
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showCreateAccount, setShowCreateAccount] = useState(false);
  const [currentPage, setCurrentPage] = useState('home'); // 'home', 'about', 'contact'

  const [newUsername, setNewUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [newUserRole, setNewUserRole] = useState('customer');

  const [authHeader, setAuthHeader] = useState(null);

  const handleLogin = async (e) => {
    e.preventDefault();
    // Create Basic Auth header
    const basicAuth = 'Basic ' + btoa(username + ':' + password);

    try {
      // Call a protected endpoint to verify credentials
      const response = await fetch('http://localhost:8080/api/users', {
        headers: {
          'Authorization': basicAuth,
        },
      });
      if (response.ok) {
        // Simulate role assignment based on username for demo purposes
        let role = null;
        if (username.toLowerCase().includes('agent')) {
          role = 'agent';
        } else if (username.toLowerCase().includes('admin')) {
          role = 'admin';
        } else {
          role = 'customer';
        }
        setUserRole(role);
        setIsLoggedIn(true);
        setAuthHeader(basicAuth);
      } else {
        alert('Login failed: Invalid credentials');
      }
    } catch (error) {
      alert('Login failed: ' + error.message);
    }
  };

  const handleLogout = () => {
    setUsername('');
    setPassword('');
    setUserRole(null);
    setIsLoggedIn(false);
    setShowCreateAccount(false);
    setAuthHeader(null);
  };

  const handleCreateAccount = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8080/api/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: newUsername,
          password: newPassword,
          role: newUserRole,
        }),
      });
      if (response.ok) {
        alert('Account created successfully');
        setNewUsername('');
        setNewPassword('');
        setNewUserRole('customer');
        setShowCreateAccount(false);
      } else {
        const errorText = await response.text();
        alert('Account creation failed: ' + errorText);
      }
    } catch (error) {
      alert('Account creation failed: ' + error.message);
    }
  };

  if (isLoggedIn) {
    let DashboardComponent = null;
    if (userRole === 'agent') {
      DashboardComponent = AgentDashboard;
    } else if (userRole === 'admin') {
      DashboardComponent = AdminDashboard;
    } else if (userRole === 'customer') {
      DashboardComponent = CustomerDashboard;
    }

    return (
      <div className="min-h-screen bg-gray-100 flex flex-col">
        {/* Navbar */}
        <nav className="bg-blue-600 text-white px-6 py-4 flex justify-between items-center">
          <div className="text-2xl font-bold">Support System</div>
          <button
            onClick={handleLogout}
            className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded transition"
          >
            Logout
          </button>
        </nav>
        {/* Dashboard */}
        <main className="flex-grow">
          <DashboardComponent authHeader={authHeader} />
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      {/* Navbar */}
      <nav className="bg-blue-600 text-white px-6 py-4 flex justify-between items-center">
        <div className="text-2xl font-bold">Support System</div>
        <div className="space-x-6">
          <button className="hover:underline" onClick={() => { setShowCreateAccount(false); setIsLoggedIn(false); setUserRole(null); setCurrentPage('home'); }}>Home</button>
          <button className="hover:underline" onClick={() => setCurrentPage('about')}>About</button>
          <button className="hover:underline" onClick={() => setCurrentPage('contact')}>Contact</button>
          <button className="hover:underline" onClick={() => { setShowCreateAccount(false); setCurrentPage('home'); }}>Login</button>
        </div>
      </nav>

      {/* Main content */}
      <main className="flex-grow flex items-center justify-center">
        {currentPage === 'home' && (
          !showCreateAccount ? (
            <form onSubmit={handleLogin} className="bg-white p-8 rounded shadow-md w-full max-w-md">
              <h2 className="text-2xl font-semibold mb-6 text-center">Login</h2>
              <div className="mb-4">
                <label htmlFor="username" className="block mb-1 font-medium">Username</label>
                <input
                  id="username"
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
              <div className="mb-6">
                <label htmlFor="password" className="block mb-1 font-medium">Password</label>
                <input
                  id="password"
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
              <button
                type="submit"
                className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
              >
                Login
              </button>
              <p className="mt-4 text-center text-sm text-gray-600">
                New user?{' '}
                <button
                  type="button"
                  onClick={() => setShowCreateAccount(true)}
                  className="text-blue-600 hover:underline"
                >
                  Create account
                </button>
              </p>
            </form>
          ) : (
            <form onSubmit={handleCreateAccount} className="bg-white p-8 rounded shadow-md w-full max-w-md">
              <h2 className="text-2xl font-semibold mb-6 text-center">Create Account</h2>
              <div className="mb-4">
                <label htmlFor="newUsername" className="block mb-1 font-medium">Username</label>
                <input
                  id="newUsername"
                  type="text"
                  value={newUsername}
                  onChange={(e) => setNewUsername(e.target.value)}
                  className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
              <div className="mb-4">
                <label htmlFor="newPassword" className="block mb-1 font-medium">Password</label>
                <input
                  id="newPassword"
                  type="password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
              <div className="mb-6">
                <label htmlFor="newUserRole" className="block mb-1 font-medium">Role</label>
                <select
                  id="newUserRole"
                  value={newUserRole}
                  onChange={(e) => setNewUserRole(e.target.value)}
                  className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="customer">Customer</option>
                  <option value="agent">Agent</option>
                  <option value="admin">Admin</option>
                </select>
              </div>
              <button
                type="submit"
                className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700 transition"
              >
                Create Account
              </button>
              <p className="mt-4 text-center text-sm text-gray-600">
                Already registered?{' '}
                <button
                  type="button"
                  onClick={() => setShowCreateAccount(false)}
                  className="text-blue-600 hover:underline"
                >
                  Login
                </button>
              </p>
            </form>
          )
        )}
      </main>
      {currentPage === 'about' && (
        <div className="max-w-4xl mx-auto p-8 bg-white rounded shadow-md text-gray-800">
          <h1 className="text-4xl font-bold mb-6 text-center text-blue-700">About This Application</h1>
          <p className="text-lg mb-4 leading-relaxed">
            Welcome to the Support System application, a comprehensive platform designed to streamline customer support interactions.
          </p>
          <p className="text-lg mb-4 leading-relaxed">
            This application offers tailored dashboards for agents, admins, and customers, providing each user role with the tools and information they need to efficiently manage support tickets and communications.
          </p>
          <p className="text-lg mb-4 leading-relaxed">
            Built with React and styled using Tailwind CSS, the application ensures a responsive and user-friendly experience across devices.
          </p>
          <p className="text-lg mb-4 leading-relaxed">
            Our goal is to enhance support team productivity and improve customer satisfaction through an intuitive and elegant interface.
          </p>
          <p className="text-center mt-8 text-blue-600 font-semibold">
            Thank you for choosing our Support System!
          </p>
        </div>
      )}
      {currentPage === 'contact' && (
        <div className="max-w-4xl mx-auto p-8 bg-white rounded shadow-md text-gray-800">
          <h1 className="text-4xl font-bold mb-6 text-center text-blue-700">Contact Us</h1>
          <p className="text-lg mb-4 leading-relaxed">
            For any inquiries or support, please reach out to us at:
          </p>
          <ul className="list-disc list-inside text-lg mb-4">
            <li>Email: support@example.com</li>
            <li>Phone: +1 (555) 123-4567</li>
            <li>Address: 123 Support St, Helpville, USA</li>
          </ul>
          <p className="text-center mt-8 text-blue-600 font-semibold">
            We look forward to assisting you!
          </p>
        </div>
      )}
    </div>
  );
}

export default App;
