import React, { useState } from 'react';
import AgentDashboard from './AgentDashboard';
import AdminDashboard from './AdminDashboard';
import CustomerDashboard from './CustomerDashboard';

function LandingPage() {
  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-8">
      <h1 className="text-4xl font-bold mb-4">Welcome to the Support System</h1>
      <p className="text-lg text-gray-700 max-w-xl text-center">
        This is the landing page. Please login or create an account to continue.
      </p>
    </div>
  );
}

function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [userRole, setUserRole] = useState(null); // 'agent', 'admin', 'customer'
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showCreateAccount, setShowCreateAccount] = useState(false);
  const [showLandingPage, setShowLandingPage] = useState(true);

  const [newUsername, setNewUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [newUserRole, setNewUserRole] = useState('customer');

  const handleLogin = (e) => {
    e.preventDefault();
    let role = null;
    if (username.toLowerCase().includes('agent')) {
      role = 'agent';
    } else if (username.toLowerCase().includes('admin')) {
      role = 'admin';
    } else {
      role = 'customer';
    }
    setUserRole(role);
    setIsLoggedIn(true);
    setShowLandingPage(false);
  };

  const handleLogout = () => {
    setUsername('');
    setPassword('');
    setUserRole(null);
    setIsLoggedIn(false);
    setShowCreateAccount(false);
    setShowLandingPage(true);
  };

  const handleCreateAccount = (e) => {
    e.preventDefault();
    console.log('Account created:', {
      username: newUsername,
      password: newPassword,
      role: newUserRole,
    });
    setNewUsername('');
    setNewPassword('');
    setNewUserRole('customer');
    setShowCreateAccount(false);
    setShowLandingPage(true);
  };

  const handleShowLogin = () => {
    setShowCreateAccount(false);
    setShowLandingPage(false);
  };

  if (isLoggedIn) {
    let DashboardComponent = null;
    if (userRole === 'agent') {
      DashboardComponent = AgentDashboard;
    } else if (userRole === 'admin') {
      DashboardComponent = AdminDashboard;
    } else if (userRole === 'customer') {
      DashboardComponent = CustomerDashboard;
    }

    return (
      <div className="min-h-screen bg-gray-100 flex flex-col">
        <nav className="bg-blue-600 text-white px-6 py-4 flex justify-between items-center">
          <div className="text-2xl font-bold cursor-pointer" onClick={handleLogout}>
            Support System
          </div>
          <button
            onClick={handleLogout}
            className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded transition"
          >
            Logout
          </button>
        </nav>
        <main className="flex-grow">
          <DashboardComponent />
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <nav className="bg-blue-600 text-white px-6 py-4 flex justify-between items-center">
        <div className="text-2xl font-bold cursor-pointer" onClick={() => setShowLandingPage(true)}>
          Support System
        </div>
        <div className="space-x-6">
          <button className="hover:underline" onClick={() => setShowLandingPage(true)}>Home</button>
          <button className="hover:underline" onClick={() => setShowLandingPage(false)}>About</button>
          <button className="hover:underline" onClick={() => setShowLandingPage(false)}>Contact</button>
          {!showCreateAccount && (
            <button className="hover:underline" onClick={handleShowLogin}>Login</button>
          )}
        </div>
      </nav>

      <main className="flex-grow flex items-center justify-center">
        {showLandingPage ? (
          <LandingPage />
        ) : !showCreateAccount ? (
          <form onSubmit={handleLogin} className="bg-white p-8 rounded shadow-md w-full max-w-md">
            <h2 className="text-2xl font-semibold mb-6 text-center">Login</h2>
            <div className="mb-4">
              <label htmlFor="username" className="block mb-1 font-medium">Username</label>
              <input
                id="username"
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-6">
              <label htmlFor="password" className="block mb-1 font-medium">Password</label>
              <input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
            >
              Login
            </button>
            <p className="mt-4 text-center text-sm text-gray-600">
              New user?{' '}
              <button
                type="button"
                onClick={() => setShowCreateAccount(true)}
                className="text-blue-600 hover:underline"
              >
                Create account
              </button>
            </p>
          </form>
        ) : (
          <form onSubmit={handleCreateAccount} className="bg-white p-8 rounded shadow-md w-full max-w-md">
            <h2 className="text-2xl font-semibold mb-6 text-center">Create Account</h2>
            <div className="mb-4">
              <label htmlFor="newUsername" className="block mb-1 font-medium">Username</label>
              <input
                id="newUsername"
                type="text"
                value={newUsername}
                onChange={(e) => setNewUsername(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-4">
              <label htmlFor="newPassword" className="block mb-1 font-medium">Password</label>
              <input
                id="newPassword"
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-6">
              <label htmlFor="newUserRole" className="block mb-1 font-medium">Role</label>
              <select
                id="newUserRole"
                value={newUserRole}
                onChange={(e) => setNewUserRole(e.target.value)}
                className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="customer">Customer</option>
                <option value="agent">Agent</option>
                <option value="admin">Admin</option>
              </select>
            </div>
            <button
              type="submit"
              className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700 transition"
            >
              Create Account
            </button>
          </form>
        )}
      </main>
    </div>
  );
}

export default App;
