// src/components/ForgotPasswordFlow.jsx

import React, { useState } from "react";
import ForgotPassword from "./ForgotPassword";
import VerifyOtp from "./VerifyOtp";
import ResetPassword from "./ResetPassword";

function ForgotPasswordFlow() {
  const [step, setStep] = useState(1);
  const [email, setEmail] = useState("");
  const [otpToken, setOtpToken] = useState("");

  return (
    <div className="flow-container">
      {step === 1 && <ForgotPassword onOtpSent={(mail) => { setEmail(mail); setStep(2); }} />}
      {step === 2 && <VerifyOtp email={email} onOtpVerified={(token) => { setOtpToken(token); setStep(3); }} />}
      {step === 3 && <ResetPassword token={otpToken} />}
    </div>
  );
}

export default ForgotPasswordFlow;
