import React, { useState, useEffect } from 'react';
import AdminSidebar from './AdminSidebar';

function AdminDashboard({ token, userId }) {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [users, setUsers] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [openTickets, setOpenTickets] = useState([]);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [editCommentId, setEditCommentId] = useState(null);
  const [editCommentText, setEditCommentText] = useState('');
  const [selectedTicketAssign, setSelectedTicketAssign] = useState(null);
  const [selectedAgent, setSelectedAgent] = useState(null);
  const [availableAgents, setAvailableAgents] = useState([]);
  const [activeSection, setActiveSection] = useState('viewUsers');
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    if (!token) return;
    const authHeader = `Bearer ${token}`;

    if (activeSection === 'viewComments') {
      setLoading(true);
      fetch('http://localhost:8080/api/comments/getcomments', {
        headers: { Authorization: authHeader }
      })
        .then(res => res.ok ? res.json() : Promise.reject('Failed to fetch comments'))
        .then(data => {
          setComments(data);
          setError(null);
        })
        .catch(err => setError(err))
        .finally(() => setLoading(false));
    } else {
      setLoading(true);
      fetch('http://localhost:8080/api/users', {
        headers: { Authorization: authHeader },
      })
        .then(res => res.ok ? res.json() : Promise.reject('Failed to fetch users'))
        .then(data => setUsers(data))
        .catch(err => setError(err))
        .finally(() => setLoading(false));

      fetch('http://localhost:8080/api/tickets/gettickets', {
        headers: { Authorization: authHeader },
      })
        .then(res => res.ok ? res.json() : Promise.reject('Failed to fetch tickets'))
        .then(data => {
          setTickets(data);
          setOpenTickets(data.filter(t => t.state === 'OPEN'));
        })
        .catch(err => setError(err))
        .finally(() => setLoading(false));
    }
  }, [token, activeSection]);

  useEffect(() => {
    if (activeSection === 'viewProfile' && userId && token) {
      setLoading(true);
      fetch(`http://localhost:8080/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then(res => res.ok ? res.json() : Promise.reject('Failed to fetch profile'))
        .then(data => setProfile(data))
        .catch(err => setError(err))
        .finally(() => setLoading(false));
    }
  }, [activeSection, userId, token]);

  useEffect(() => {
    if (!token) return;
    fetch('http://localhost:8080/api/users/agents', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => res.ok ? res.json() : Promise.reject('Failed to fetch agents'))
      .then(data => setAvailableAgents(data))
      .catch(err => setError(err));
  }, [token]);

  const handleAddComment = () => {
    if (newComment.trim() && selectedTicketAssign) {
      fetch('http://localhost:8080/api/comments', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content: newComment,
          ticket: { id: selectedTicketAssign },
        }),
      })
        .then(res => res.ok ? res.json() : Promise.reject('Failed to add comment'))
        .then(data => {
          setComments(prev => [...prev, data]);
          setNewComment('');
        })
        .catch(err => setError(err));
    }
  };

  const handleEditComment = (id, text) => {
    setEditCommentId(id);
    setEditCommentText(text);
  };

  const handleUpdateComment = () => {
    if (editCommentText.trim() && editCommentId !== null) {
      fetch(`http://localhost:8080/api/comments/${editCommentId}`, {
        method: 'PUT',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ content: editCommentText }),
      })
        .then(res => res.ok ? res.json() : Promise.reject('Failed to update comment'))
        .then(data => {
          setComments(prev => prev.map(c => c.id === editCommentId ? data : c));
          setEditCommentId(null);
          setEditCommentText('');
        })
        .catch(err => setError(err));
    }
  };

  const deleteComment = id => {
    fetch(`http://localhost:8080/api/comments/${id}`, {
      method: 'DELETE',
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(() => setComments(prev => prev.filter(c => c.id !== id)))
      .catch(err => setError(err));
  };

  const assignTicket = () => {
    if (selectedTicketAssign && selectedAgent) {
      fetch(`http://localhost:8080/api/tickets/${selectedTicketAssign}/assign/${selectedAgent}`, {
        method: 'PUT',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      })
        .then(res => res.ok ? res.json() : Promise.reject('Failed to assign ticket'))
        .then(() => {
          setTickets(prev => prev.map(t => t.id === parseInt(selectedTicketAssign)
            ? { ...t, assignedAgent: { id: parseInt(selectedAgent) } } : t));
          setSelectedTicketAssign(null);
          setSelectedAgent(null);
        })
        .catch(err => setError(err));
    }
  };

  return (
    <div className="flex min-h-screen font-poppins bg-gray-100">
      <AdminSidebar onSelect={setActiveSection} />
      <main className="flex-grow p-8 ml-64 max-w-6xl mx-auto">
        {error && <p className="text-red-600 mb-4">{error}</p>}

        {activeSection === 'viewUsers' && (
          <div><h2>Users</h2><ul>{users.map(u => <li key={u.id}>{u.username} - {u.role}</li>)}</ul></div>
        )}

        {activeSection === 'viewTickets' && (
          <div><h2>Tickets</h2><ul>{tickets.map(t => <li key={t.id}>{t.title}</li>)}</ul></div>
        )}

        {activeSection === 'assignTickets' && (
          <div>
            <h2>Assign Tickets</h2>
            <select value={selectedTicketAssign || ''} onChange={e => setSelectedTicketAssign(e.target.value)}>
              <option value="" disabled>Select a ticket</option>
              {openTickets.map(t => <option key={t.id} value={t.id}>{t.title}</option>)}
            </select>
            <select value={selectedAgent || ''} onChange={e => setSelectedAgent(e.target.value)}>
              <option value="" disabled>Select an agent</option>
              {availableAgents.map(a => <option key={a.id} value={a.id}>{a.username}</option>)}
            </select>
            <button onClick={assignTicket}>Assign</button>
          </div>
        )}

        {activeSection === 'viewProfile' && profile && (
          <div>
            <h2>Profile</h2>
            <p>ID: {profile.id}</p>
            <p>Username: {profile.username}</p>
            <p>Email: {profile.email}</p>
            <p>Role: {profile.role}</p>
          </div>
        )}

        {activeSection === 'viewComments' && (
          <div>
            <h2>Comments</h2>
            <ul>
              {comments.map(c => (
                <li key={c.id}>
                  {editCommentId === c.id ? (
                    <>
                      <textarea value={editCommentText} onChange={e => setEditCommentText(e.target.value)} />
                      <button onClick={handleUpdateComment}>Update</button>
                    </>
                  ) : (
                    <>
                      {c.content} <button onClick={() => handleEditComment(c.id, c.content)}>Edit</button>
                    </>
                  )}
                  <button onClick={() => deleteComment(c.id)}>Delete</button>
                </li>
              ))}
            </ul>
            <textarea value={newComment} onChange={e => setNewComment(e.target.value)} />
            <button onClick={handleAddComment}>Add Comment</button>
          </div>
        )}
      </main>
    </div>
  );
}

export default AdminDashboard;
