import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar';

function CustomerDashboard({ token, userId }) {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [newTicketTitle, setNewTicketTitle] = useState('');
  const [newTicketDescription, setNewTicketDescription] = useState('');
const [selectedTicketId, setSelectedTicketId] = useState(null);
const [selectedTicketForComments, setSelectedTicketForComments] = useState(null);
  const [newComment, setNewComment] = useState('');
  const [editCommentId, setEditCommentId] = useState(null);
  const [editCommentText, setEditCommentText] = useState('');
  const [activeView, setActiveView] = useState('create');

  console.log("CustomerDashboard userId:", userId);
  console.log("CustomerDashboard activeView:", activeView);
  console.log("CustomerDashboard tickets:", tickets);

  const [userProfile, setUserProfile] = useState(null);
  const [profileLoading, setProfileLoading] = useState(false);
  const [profileError, setProfileError] = useState(null);

  const [myTicketComments, setMyTicketComments] = useState([]);
  const [showTicketCommentsFor, setShowTicketCommentsFor] = useState(null);

  // New state for password change
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [passwordChangeError, setPasswordChangeError] = useState('');
  const [passwordChangeSuccess, setPasswordChangeSuccess] = useState('');

  // Fetch user profile
  useEffect(() => {
    if (!userId || !token) return;
    setProfileLoading(true);
    fetch(`http://localhost:8080/api/users/${userId}`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch user profile');
        return res.json();
      })
      .then(profile => {
        setUserProfile(profile);
        setProfileError(null);
      })
      .catch(err => setProfileError(err.message))
      .finally(() => setProfileLoading(false));
  }, [userId, token]);

    // Fetch tickets for user
    useEffect(() => {
      if (!token) return;
      setLoading(true);
      fetch(`http://localhost:8080/api/tickets/customer`, {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then(res => {
          if (!res.ok) throw new Error('Failed to fetch tickets');
          return res.json();
        })
        .then(async (ticketsData) => {
          // For each ticket, fetch comments with userRole included
          const ticketsWithComments = await Promise.all(ticketsData.map(async (ticket) => {
            const commentsRes = await fetch(`http://localhost:8080/api/comments/ticket/${ticket.id}/ordered`, {
              headers: { Authorization: `Bearer ${token}` },
            });
            const comments = commentsRes.ok ? await commentsRes.json() : [];
            return { ...ticket, comments };
          }));
          setTickets(ticketsWithComments);
          setError(null);
        })
        .catch(err => setError(err.message))
        .finally(() => setLoading(false));
    }, [token]);

  // Create a new ticket
  const handleCreateTicket = () => {
    if (newTicketTitle.trim() && newTicketDescription.trim()) {
      fetch('http://localhost:8080/api/tickets', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          title: newTicketTitle,
          description: newTicketDescription,
          state: 'OPEN',
        }),
      })
        .then(res => {
          if (!res.ok) throw new Error('Failed to create ticket');
          return res.json();
        })
        .then(newTicket => {
          setTickets(prev => [...prev, newTicket]);
          setNewTicketTitle('');
          setNewTicketDescription('');
          setError(null);
        })
        .catch(err => setError(err.message));
    }
  };

  // Add a comment
  const handleAddComment = () => {
    if (newComment.trim() && selectedTicketId) {
      fetch(`http://localhost:8080/api/comments/ticket/${selectedTicketId}`, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content: newComment,
        }),
      })
        .then(res => {
          if (!res.ok) throw new Error('Failed to add comment');
          return res.json();
        })
        .then(newCommentData => {
          // Ensure userRole is set for new comment for proper styling
          if (!newCommentData.userRole && newCommentData.user) {
            // Fallback: assign userRole if missing (optional, depends on API)
            newCommentData.userRole = newCommentData.user.role || 'CUSTOMER';
          }
          setTickets(prev =>
            prev.map(ticket =>
              ticket.id === selectedTicketId
                ? {
                    ...ticket,
                    comments: [...(ticket.comments || []), newCommentData],
                  }
                : ticket
            )
          );
          setNewComment('');
          setError(null);
          // Force re-render by toggling selectedTicketForComments
          setSelectedTicketForComments(null);
          setTimeout(() => setSelectedTicketForComments(selectedTicketId), 0);
        })
        .catch(err => setError(err.message));
    }
  };

  // Edit comment preparation
  const handleEditComment = (ticketId, commentId, text) => {
    setSelectedTicketId(ticketId);
    setEditCommentId(commentId);
    setEditCommentText(text);
  };

  // Update comment
  const handleUpdateComment = () => {
    if (editCommentText.trim() && selectedTicketId && editCommentId !== null) {
      fetch(`http://localhost:8080/api/comments/${editCommentId}`, {
        method: 'PUT',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ content: editCommentText }),
      })
        .then(res => {
          if (!res.ok) throw new Error('Failed to update comment');
          return res.json();
        })
        .then(() => {
          setTickets(prev =>
            prev.map(ticket =>
              ticket.id === selectedTicketId
                ? {
                    ...ticket,
                    comments: ticket.comments.map(comment =>
                      comment.id === editCommentId
                        ? { ...comment, content: editCommentText, userRole: comment.userRole || 'CUSTOMER' }
                        : comment
                    ),
                  }
                : ticket
            )
          );
          setEditCommentId(null);
          setEditCommentText('');
          setError(null);
        })
        .catch(err => setError(err.message));
    }
  };

  // Define the missing handleChangePassword function
  const handleChangePassword = () => {
    setPasswordChangeError('');
    setPasswordChangeSuccess('');

    if (newPassword !== confirmNewPassword) {
      setPasswordChangeError('New passwords do not match');
      return;
    }

    fetch(`http://localhost:8080/api/users/${userId}/change-password`, {
      method: 'PUT',
      headers: {
        Authorization: `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        oldPassword: oldPassword,
        newPassword: newPassword,
      }),
    })
      .then(res => {
        if (!res.ok) {
          throw new Error('Failed to change password');
        }
        return res.text();
      })
      .then(message => {
        setPasswordChangeSuccess(message);
        setOldPassword('');
        setNewPassword('');
        setConfirmNewPassword('');
      })
      .catch(err => setPasswordChangeError(err.message));
  };

  return (
    <div className="flex min-h-screen font-poppins bg-gray-100">
      <Sidebar activeView={activeView} setActiveView={setActiveView} />
      <main className="flex-grow p-8 ml-64">
        <h1 className="text-3xl font-bold mb-6 text-blue-900">Customer Dashboard</h1>
        {loading && <p>Loading...</p>}
        {activeView === 'profile' && error && <p className="text-red-600 mb-4">{error}</p>}

        {activeView === 'create' && (
          <div className="mb-6 max-w-3xl">
            <h2 className="text-2xl font-semibold mb-4 text-blue-800">Create Ticket</h2>
            <input
              type="text"
              placeholder="Title"
              value={newTicketTitle}
              onChange={(e) => setNewTicketTitle(e.target.value)}
              className="border border-gray-300 px-3 py-2 rounded mb-3 w-full"
            />
            <textarea
              placeholder="Description"
              value={newTicketDescription}
              onChange={(e) => setNewTicketDescription(e.target.value)}
              className="border border-gray-300 px-3 py-2 rounded mb-3 w-full"
              rows="4"
            />
            <button
              onClick={handleCreateTicket}
              className="bg-blue-600 text-white px-5 py-2 rounded hover:bg-blue-700 transition"
            >
              Create Ticket
            </button>
          </div>
        )}

        {activeView === 'view' && (
          <div className="max-w-4xl space-y-6">
            <h2 className="text-2xl font-semibold mb-4 text-blue-800">View Tickets</h2>
                {tickets.length === 0 ? (
                  <p>No tickets found.</p>
                ) : (
                  tickets.map(ticket => {
                    console.log("Ticket createdBy.id:", ticket.createdBy ? ticket.createdBy.id : null, "UserId:", userId, "Ticket state:", ticket.state);
                    return (
                      <div key={ticket.id} className="border p-4 rounded shadow bg-white">
                        <h3 className="text-xl font-semibold">{ticket.title}</h3>
                        <p className="mb-1">Status: <strong>{ticket.state}</strong></p>
                        <p className="text-gray-700 mb-3">{ticket.description}</p>
                        {(ticket.createdBy && ticket.createdBy.id === Number(userId) && (ticket.state === 'OPEN' || ticket.state === 'CLOSED')) && (
                          <button
                            onClick={() => {
                              fetch(`http://localhost:8080/api/tickets/${ticket.id}`, {
                                method: 'DELETE',
                                headers: {
                                  'Authorization': `Bearer ${token}`,
                                },
                              })
                                .then(() => {
                                  setTickets(tickets.filter(t => t.id !== ticket.id));
                                })
                                .catch(err => console.error('Error deleting ticket:', err));
                            }}
                            className="bg-red-600 text-white px-3 py-1 rounded"
                          >
                            Delete
                          </button>
                        )}
                      </div>
                    );
                  })
                )}
          </div>
        )}

{activeView === 'comments' && (
          <div className="max-w-4xl">
            <h2 className="text-2xl font-semibold mb-4 text-blue-800">Comments</h2>
            {tickets.length === 0 ? (
              <p>No tickets found.</p>
            ) : (
              <>
                <div className="mb-4">
                  <label htmlFor="ticketSelect" className="block mb-1 font-medium">Select Ticket</label>
                  <select
                    id="ticketSelect"
                    value={selectedTicketForComments || ''}
                    onChange={(e) => setSelectedTicketForComments(e.target.value)}
                    className="border border-gray-300 rounded px-3 py-2 w-full max-w-xs"
                  >
                    <option value="" disabled>Select a ticket</option>
                    {tickets.map(ticket => (
                      <option key={ticket.id} value={ticket.id}>
                        {ticket.title} (ID: {ticket.id})
                      </option>
                    ))}
                  </select>
                </div>
                <div className="border p-4 rounded shadow bg-white mb-6 max-h-96 overflow-y-auto px-2">
                  {(tickets.find(t => String(t.id) === String(selectedTicketForComments))?.comments || []).map(comment => {
                    const isAgent = comment.userRole === 'AGENT';
                    return (
                      <div
                        key={comment.id}
                        className={`flex ${isAgent ? 'justify-start' : 'justify-end'}`}
                      >
                        <div
                          className={`max-w-xs px-4 py-2 rounded-lg shadow ${isAgent ? 'bg-blue-200 text-blue-900 rounded-bl-none border border-blue-400' : 'bg-green-500 text-white rounded-br-none border border-green-700'}`}
                        >
                          <p className="whitespace-pre-wrap">{comment.content}</p>
                          <small className="block mt-1 text-xs text-gray-600">
                            {new Date(comment.createdAt).toLocaleString()}
                          </small>
                          <div className="mt-1 text-xs font-semibold">
                            {isAgent ? 'Agent' : 'Customer'}
                          </div>
                        </div>
                      </div>
                    );
                  })}
                  <div className="flex space-x-2 mt-3">
                    <input
                      type="text"
                      placeholder="Add a comment"
                      value={selectedTicketId === selectedTicketForComments ? newComment : ''}
                      onChange={(e) => {
                        setSelectedTicketId(selectedTicketForComments);
                        setNewComment(e.target.value);
                      }}
                      className="border px-2 py-1 rounded flex-grow"
                    />
                    <button
                      onClick={handleAddComment}
                      className="bg-blue-600 text-white px-4 py-1 rounded"
                      disabled={selectedTicketId !== selectedTicketForComments}
                    >
                      Add
                    </button>
                  </div>
                </div>
              </>
            )}
          </div>
        )}
        {activeView === 'profile' && (
          <div className="max-w-3xl bg-white p-6 rounded shadow">
            <h2 className="text-2xl font-semibold mb-4 text-blue-800">User Profile</h2>
            {userProfile ? (
              <div className="space-y-4 text-gray-700">
                <p><strong>Name:</strong> {userProfile.name || userProfile.username}</p>
                <p><strong>Email:</strong> {userProfile.email}</p>
                <p><strong>Username:</strong> {userProfile.username}</p>
                {/* Add more profile fields as needed */}
                <hr className="my-4" />
                <h3 className="text-xl font-semibold mb-2">Change Password</h3>
                <form
                  onSubmit={e => {
                    e.preventDefault();
                    handleChangePassword();
                  }}
                >
                  <div className="mb-3">
                    <label className="block mb-1 font-medium">Old Password</label>
                    <input
                      type="password"
                      value={oldPassword}
                      onChange={e => setOldPassword(e.target.value)}
                      className="border border-gray-300 rounded px-3 py-2 w-full"
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="block mb-1 font-medium">New Password</label>
                    <input
                      type="password"
                      value={newPassword}
                      onChange={e => setNewPassword(e.target.value)}
                      className="border border-gray-300 rounded px-3 py-2 w-full"
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="block mb-1 font-medium">Confirm New Password</label>
                    <input
                      type="password"
                      value={confirmNewPassword}
                      onChange={e => setConfirmNewPassword(e.target.value)}
                      className="border border-gray-300 rounded px-3 py-2 w-full"
                      required
                    />
                  </div>
                  {passwordChangeError && (
                    <p className="text-red-600 mb-3">{passwordChangeError}</p>
                  )}
                  {passwordChangeSuccess && (
                    <p className="text-green-600 mb-3">{passwordChangeSuccess}</p>
                  )}
                  <button
                    type="submit"
                    className="bg-blue-600 text-white px-5 py-2 rounded hover:bg-blue-700 transition"
                  >
                    Change Password
                  </button>
                </form>
              </div>
            ) : (
              <p>Loading profile...</p>
            )}
          </div>
        )}
      </main>
    </div>
  );
}

export default CustomerDashboard;
