import React, { useState } from "react";
import axios from "axios";

function ForgotPassword({ onOtpSent }) {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:8080/api/auth/forgot-password", { email });
      setMessage(res.data.message || "OTP sent successfully!");
      onOtpSent(email);
    } catch (error) {
      setMessage(error.response?.data?.message || "Error sending OTP.");
    }
  };

  const handleBackToLogin = () => {
    window.location.href = "/login";
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center">
      <div className="bg-white p-6 rounded-xl shadow-lg max-w-sm w-full">
        <h2 className="text-xl font-bold mb-4">Forgot Password</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="email"
            placeholder="Enter your registered email"
            className="w-full px-4 py-2 border border-gray-300 rounded-lg"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <button
            type="submit"
            className="w-full bg-purple-700 text-white py-2 rounded-lg hover:bg-blue-800"
          >
            Send OTP
          </button>
        </form>
        {message && <p className="text-sm text-green-600 mt-3">{message}</p>}
        <button
          onClick={handleBackToLogin}
          className="mt-6 bg-gray-300 text-gray-700 py-2 px-4 rounded w-48"
        >
          Back to Login
        </button>
      </div>
    </div>
  );
}

export default ForgotPassword;
